mapper.select:
select * from (
	select rownum rnum, a.* from (
		select * from reviewBoard 
			where postDisable = 0 and petType = #{petType}
			order by postId desc
	) a 
) where rnum between #{start} and #{end}

select * from reviewBoard 
where postDisable = 0 and userId = #{value}
order by postId desc
